mapper.select:
select
    member_id, member_name, member_phonenumper, sex, member_password, member_card_id, member_card_name, member_type, member_type_name, recharge_amount, give_amount, single_amount, surplus_amount, surplus_times, payment_type, artificer_id, artificer_name, member_points, additive, additive_number, card_personnel, remark, create_time, create_nema, modify_name, modify_time,
    (SELECT count(member_id) FROM b_member_info WHERE member_phonenumper=bmi.member_phonenumper GROUP BY bmi.member_phonenumper ) handle_card_num
from
    b_member_info bmi

     and member_name like concat('%', #{memberName}, '%')
     and member_phonenumper like concat('%', #{memberPhonenumper}, '%')
     and sex = #{sex}
     and member_password = #{memberPassword}
     and member_card_id = #{memberCardId}
     and member_card_name like concat('%', #{memberCardName}, '%')
     and member_type = #{memberType}
     and member_type_name like concat('%', #{memberTypeName}, '%')
     and recharge_amount = #{rechargeAmount}
     and give_amount = #{giveAmount}
     and single_amount = #{singleAmount}
     and surplus_amount = #{surplusAmount}
     and surplus_times = #{surplusTimes}
     and payment_type = #{paymentType}
     and artificer_id = #{artificerId}
     and artificer_name like concat('%', #{artificerName}, '%')
     and member_points = #{memberPoints}
     and additive = #{additive}
     and additive_number = #{additiveNumber}
     and card_personnel = #{cardPersonnel}
     and create_nema = #{createNema}
     and modify_name like concat('%', #{modifyName}, '%')
     and modify_time = #{modifyTime}
     and member_name like concat('%', #{memberName}, '%')
     and (member_phonenumper like concat('%', #{searchValue}, '%') OR member_name like concat('%', #{memberName}, '%'))

mapper.update:
update b_member_info
member_name = #{memberName}, member_phonenumper = #{memberPhonenumper}, sex = #{sex}, member_password = #{memberPassword}, member_card_id = #{memberCardId}, member_card_name = #{memberCardName}, member_type = #{memberType}, member_type_name = #{memberTypeName}, recharge_amount = #{rechargeAmount}, give_amount = #{giveAmount}, single_amount = #{singleAmount}, surplus_amount = #{surplusAmount}, surplus_times = #{surplusTimes}, payment_type = #{paymentType}, artificer_id = #{artificerId}, artificer_name = #{artificerName}, member_points = #{memberPoints}, additive = #{additive}, additive_number = #{additiveNumber}, card_personnel = #{cardPersonnel}, remark = #{remark}, create_time = #{createTime}, create_nema = #{createNema}, modify_name = #{modifyName}, modify_time = #{modifyTime},
where member_id = #{memberId}
